function coreoExport(variable, value) {
    if (! /^[A-Za-z]+[A-Za-z0-9_-]*/.test(variable)) {
        throw(`${variable} is not a valid variable name`);
    }

    console.log(`CC_EXPORT_OUTPUT_STARTS_HERE`);
    console.log(`${variable}=${value}`);
    console.log(`CC_EXPORT_OUTPUT_ENDS_HERE`);
}

