    if (! /^[A-Za-z]+[A-Za-z0-9_-]*/.test(variable)) {
        throw(`${variable} is not a valid variable name`);
    }

    console.log(`CC_EXPORT_OUTPUT_STARTS_HERE`);
    console.log(`${variable}=${value}`);
    console.log(`CC_EXPORT_OUTPUT_ENDS_HERE`);
}

function myfunc(json_input, callback) {
var rollup_string = "";
let rollup = '';
let emailText = '';
let numberOfViolations = 0;
for (var entry=0; entry < json_input.length; entry++) {
    if (json_input[entry]['endpoint']['to'].length) {
        numberOfViolations += parseInt(json_input[entry]['num_violations']);
        emailText += "recipient: " + json_input[entry]['endpoint']['to'] + " - " + "nViolations: " + json_input[entry]['num_violations'] + "
";
