		const notifiers = [];
		const email = "" || 'NONE';
		const endpoint = { 'to': email };

		const notifier = this.createNotifier(endpoint);

		if (email != "NONE") {
			notifiers.push(notifier);
		}

		return notifiers;
	}

	getNotifiers() {
		return this.notifiers;
	}

	getHTML() {
		return this.HTML;
	}

}

